import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [data, setData] = useState([]);
  const [selectedState, setSelectedState] = useState('State1'); // Default state

  useEffect(() => {
    // Fetch data from the Flask backend API with the selected state
    axios.get('http://localhost:5000/api/data', { params: { state: selectedState } })
      .then(response => setData(response.data))
      .catch(error => console.log(error));
  }, [selectedState]); // Fetch data whenever the selectedState changes

  const handleStateChange = (event) => {
    setSelectedState(event.target.value); // Update the selectedState when the dropdown value changes
  };

  return (
    <div>
      <h1>My App</h1>
      <div>
        <label htmlFor="stateDropdown">Select State:</label>
        <select id="stateDropdown" value={selectedState} onChange={handleStateChange}>
          <option value="State1">State1</option>
          <option value="State2">State2</option>
          {/* Add more options for other states */}
        </select>
      </div>
      <ul>
        {data.map(item => <li key={item.Crop_Year}>{item.Annual_Rainfall}</li>)}
      </ul>
    </div>
  );
};

export default App;
