import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dropdown = ({ options, value, onChange }) => (
  <select value={value} onChange={onChange}>
    {options.map((state) => (
      <option key={state} value={state}>
        {state}
      </option>
    ))}
  </select>
);

const App = () => {
  const [fetchedData, setFetchedData] = useState([]); // Separate state for fetched data
  const [selectedState, setSelectedState] = useState('');
  const [loadingStates, setLoadingStates] = useState(true);
  const [loadingData, setLoadingData] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/states')
      .then(response => {
        if (response.data.length > 0) {
          setSelectedState(response.data[0]);
        }
        setLoadingStates(false);
      })
      .catch(error => {
        setError('Error fetching states.');
        setLoadingStates(false);
      });
  }, []);

  const fetchDataForSelectedState = React.useCallback(() => {
    setLoadingData(true);
    setError('');

    axios.get('http://localhost:5000/api/data', { params: { state: selectedState } })
      .then(response => {
        setFetchedData(response.data);
        setLoadingData(false);
      })
      .catch(error => {
        setError('Error fetching data.');
        setLoadingData(false);
      });
  }, [selectedState]);

  useEffect(() => {
    if (selectedState !== '') {
      fetchDataForSelectedState();
    }
  }, [selectedState]);

  const handleStateChange = (event) => {
    setSelectedState(event.target.value);
  };

  return (
    <div>
      <h1>My App</h1>
      <div>
        <label htmlFor="stateDropdown">Select State:</label>
        {loadingStates ? (
          <div>Loading states...</div>
        ) : (
          <Dropdown options={fetchedData} value={selectedState} onChange={handleStateChange} />
        )}
        {error && <div>Error: {error}</div>}
      </div>
      {!loadingStates && (
        <>
          {loadingData ? (
            <div>Loading data...</div>
          ) : fetchedData.length > 0 ? (
            <ul>
              {fetchedData.map(item => (
                <li key={item.Crop_Year}>{item.Annual_Rainfall}</li>
              ))}
            </ul>
          ) : (
            <div>No data available for the selected state.</div>
          )}
        </>
      )}
    </div>
  );
};

export default App;
