import React, { useState, useEffect } from "react";
import axios from "axios";
import Select from "react-select";

const App = () => {
  const [selectedState, setSelectedState] = useState("");
  const [plotUrl, setPlotUrl] = useState("");
  const [stateOptions, setStateOptions] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/states")
      .then((response) => {
        if (response.data.length > 0) {
          const firstState = response.data[0]; // Get the first state from the fetched data
          setSelectedState(firstState); // Set the first state as the initial selected state
        } else {
          console.log("No states found.");
        }
        setStateOptions(
          response.data.map((state) => ({ label: state, value: state }))
        );
      })
      .catch((error) => {
        console.error("Error fetching states:", error);
      });
  }, []);

  const handleGeneratePlot = () => {
    axios
      .get(`http://localhost:5000/api/rainfall_plot?state=${selectedState}`)
      .then((response) => {
        // Assuming the response contains the URL of the saved plot image
        setPlotUrl(response.data.plot_url);
      })
      .catch((error) => {
        console.error("Error generating plot:", error);
      });
  };

  const handleShowPlot = () => {
    if (selectedState !== "") {
      handleGeneratePlot();
    }
  };

  useEffect(() => {
    console.log("Plot URL:", plotUrl);
  }, [plotUrl]);

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-r from-slate-900 to-purple-900">
      {/* Navigation Bar */}
      <nav className="bg-gradient-to-r from-purple-800 to-indigo-800 p-4">
        <div className="container mx-auto flex items-center justify-between">
          {/* Logo */}
          <div className="">
            <h2 className="text-white text-2xl font-bold">Rainfall-Explorer</h2>
          </div>

          {/* Navigation Menu */}
          <ul className="flex space-x-4 text-white">
            <li>
              <a href="#home">Home</a>
            </li>
            <li>
              <a href="#about">About</a>
            </li>
            <li>
              <a href="#contact">Contact</a>
            </li>
          </ul>
        </div>
      </nav>

      {/* Main Content */}
      <main className="flex-grow container mx-auto p-8">
        {/* Header */}
        <header className="text-center mb-8">
          <h1 className="text-white text-4xl font-bold">
            Rainfall Data Visualization
          </h1>
          <p className="text-orange-500 text-lg">
            Explore annual rainfall data for different states in India.
          </p>
        </header>


{/* Input and Button */}
<div className="flex items-center justify-center mb-4">
  <div className="relative">
    <Select
      options={stateOptions}
      value={{ label: selectedState, value: selectedState }}
      onChange={(selectedOption) => setSelectedState(selectedOption.value)}
      placeholder="Enter state name"
      className="bg-gray-100 border rounded-md py-2 px-4 pl-8 pr-12 text-gray-800 focus:outline-none focus:border-blue-500"
      styles={{
        indicatorSeparator: (base) => ({
          ...base,
          display: "none", // Hide the default separator line
        }),
        dropdownIndicator: (base) => ({
          ...base,
          display: "none", // Hide the default dropdown arrow
        }),
        option: (base, { isFocused }) => ({
          ...base,
          backgroundColor: isFocused ? "#EDF2F7" : "transparent", // Change the option background color on focus
          color: isFocused ? "gray" : "black", // Change the option text color on focus
        }),
      }}
    />
    <div className="absolute inset-y-0 left-2 flex items-center pointer-events-none">
      <svg
        className="w-5 h-5 text-gray-800"
        fill="none"
        viewBox="0 0 24 24"
        stroke="currentColor"
      >
        <path
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth={2}
          d="M12 4l-4 4m0 0l4 4m-4-4h14"
        />
      </svg>
    </div>
  </div>

  <button
    className="bg-gradient-to-r from-green-400 to-blue-500 hover:from-blue-500 hover:to-blue-600 text-white py-2 px-4 rounded-md transition duration-300 shadow-md focus:ring-2 focus:ring-blue-400 focus:outline-none ml-2"
    onClick={handleShowPlot}
  >
    Show Plot
  </button>
</div>


        {/* Display the plot image if available */}
        {plotUrl !== "" && (
          <div className="text-center">
            <h2 className="text-white text-2xl font-bold mb-4">
              Rainfall Plot for {selectedState}
            </h2>
            <img
              src={`http://localhost:5000/${plotUrl}`}
              alt="Rainfall Plot"
              className="max-w-full"
            />
          </div>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-gradient-to-r from-purple-900 to-indigo-800 p-4">
        <div className="container mx-auto text-white text-center">
          <p className="text-sm">
            &copy; {new Date().getFullYear()} RainfallExplorer. All rights
            reserved.
          </p>
          <p className="text-sm">Made with ❤️ using React and Flask.</p>
        </div>
      </footer>
    </div>
  );
};

export default App;
