import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [selectedState, setSelectedState] = useState('');
  const [plotUrl, setPlotUrl] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/states')
      .then(response => {
        if (response.data.length > 0) {
          const firstState = response.data[0]; // Get the first state from the fetched data
          setSelectedState(firstState); // Set the first state as the initial selected state
        } else {
          console.log('No states found.');
        }
      })
      .catch(error => {
        console.error('Error fetching states:', error);
      });
  }, []);

  const handleStateChange = (event) => {
    setSelectedState(event.target.value);
    setPlotUrl(''); // Clear the plot URL when the state is changed
  };

  const handleGeneratePlot = () => {
    axios.get(`http://localhost:5000/api/rainfall_plot?state=${selectedState}`)
      .then(response => {
        // Assuming the response contains the URL of the saved plot image
        setPlotUrl(response.data.plot_url);
      })
      .catch(error => {
        console.error('Error generating plot:', error);
      });
  };

  const handleShowPlot = () => {
    if (selectedState !== '') {
      handleGeneratePlot();
    }
  };

  useEffect(() => {
    console.log('Plot URL:', plotUrl);
  }, [plotUrl]);

  return (
    <div className="bg-gradient-to-r from-gray-600 to-slate-600 min-h-screen p-8">
      <header className="text-center mb-8">
        <h1 className="text-white text-4xl font-bold">Rainfall Data Visualization</h1>
        <p className="text-white text-lg">Explore annual rainfall data for different states in India.</p>
      </header>
      <div className="flex items-center justify-center mb-4">
        <input
          type="text"
          className="border border-gray-300 rounded-md py-2 px-4 mr-2"
          value={selectedState}
          onChange={handleStateChange}
          placeholder="Enter state name"
        />
        <button
          className="bg-blue-500 text-white py-2 px-4 rounded-md"
          onClick={handleShowPlot}
        >
          Show Plot
        </button>
      </div>
      {/* Display the plot image if the URL is available */}
      {plotUrl !== '' && (
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Rainfall Plot for {selectedState}</h2>
          <img src={`http://localhost:5000/${plotUrl}`} alt="Rainfall Plot" />
        </div>
      )}
    </div>
  );
};

export default App;
