import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const App = () => {
  const [fetchedData, setFetchedData] = useState([]);
  const [selectedState, setSelectedState] = useState('');
  const [plotUrl, setPlotUrl] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/states')
      .then(response => {
        if (response.data.length > 0) {
          const firstState = response.data[0]; // Get the first state from the fetched data
          setSelectedState(firstState); // Set the first state as the initial selected state
          setFetchedData(response.data);
        } else {
          console.log('No states found.');
        }
      })
      .catch(error => {
        console.error('Error fetching states:', error);
      });
  }, []);

  const fetchDataForSelectedState = useCallback(() => {
    axios.get('http://localhost:5000/api/data', { params: { state: selectedState } })
      .then(response => {
        setFetchedData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, [selectedState]);

  useEffect(() => {
    if (selectedState !== '') {
      fetchDataForSelectedState();
    }
  }, [selectedState, fetchDataForSelectedState]);

  const handleStateChange = (event) => {
    setSelectedState(event.target.value);
    setPlotUrl(''); // Clear the plot URL when the state is changed
  };

  const handleGeneratePlot = () => {
    axios.get('http://localhost:5000/api/rainfall_plot', { params: { state: selectedState } })
      .then(response => {
        // Assuming the response contains the URL of the saved plot image
        setPlotUrl(response.data.plot_url);
      })
      .catch(error => {
        console.error('Error generating plot:', error);
      });
  };

  const handleShowPlot = () => {
    if (selectedState !== '') {
      handleGeneratePlot(); // Call the plot generation function on button click
    }
  };

  return (
    <div>
      <h1>My App</h1>
      <div>
        <label htmlFor="stateInput">Enter State:</label>
        <input
          type="text"
          id="stateInput"
          value={selectedState}
          onChange={handleStateChange}
          placeholder="Enter state name"
        />
        <button onClick={handleShowPlot}>Show Plot</button>
      </div>
      {fetchedData.length > 0 ? (
        <ul>
          {fetchedData.map(item => (
            <li key={item.Crop_Year}>{item.Annual_Rainfall}</li>
          ))}
        </ul>
      ) : (
        fetchedData.length === 0 && selectedState !== '' ? (
          <div>No data available for the selected state.</div>
        ) : null
      )}
      {plotUrl !== '' && <img src={plotUrl} alt="Rainfall Plot" />}
    </div>
  );
};

export default App;
