import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [selectedState, setSelectedState] = useState('');
  const [plotUrl, setPlotUrl] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/states')
      .then(response => {
        if (response.data.length > 0) {
          const firstState = response.data[0]; // Get the first state from the fetched data
          setSelectedState(firstState); // Set the first state as the initial selected state
        } else {
          console.log('No states found.');
        }
      })
      .catch(error => {
        console.error('Error fetching states:', error);
      });
  }, []);

  const handleStateChange = (event) => {
    setSelectedState(event.target.value);
    setPlotUrl(''); // Clear the plot URL when the state is changed
  };

  const handleGeneratePlot = () => {
    axios.get(`http://localhost:5000/api/rainfall_plot?state=${selectedState}`)
      .then(response => {
        // Assuming the response contains the URL of the saved plot image
        setPlotUrl(response.data.plot_url);
      })
      .catch(error => {
        console.error('Error generating plot:', error);
      });
  };

  const handleShowPlot = () => {
    if (selectedState !== '') {
      handleGeneratePlot();
    }
  };

  useEffect(() => {
    console.log('Plot URL:', plotUrl);
  }, [plotUrl]);

  return (
    <div>
      {/* Navigation Bar */}
      <nav className="bg-gradient-to-r from-slate-900 to-purple-900 p-4">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-white text-4xl font-bold">Rainfall Data Visualization</h1>
          <ul className="flex">
            <li className="mr-4">
              <a href="#" className="text-white">Home</a>
            </li>
            <li className="mr-4">
              <a href="#" className="text-white">About</a>
            </li>
            <li>
              <a href="#" className="text-white">Contact</a>
            </li>
          </ul>  
        </div>
      </nav>

      {/* Header */}
      <header className="text-center my-8">
        <h1 className="text-white text-4xl font-bold">Rainfall Data Visualization</h1>
        <p className="text-orange-500 text-lg">Explore annual rainfall data for different states in India.</p>
      </header>

      {/* Main Content - Body */}
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-center mb-4">
          <input
            type="text"
            className="border border-gray-300 rounded-md py-2 px-4 mr-2 focus:outline-none focus:border-blue-500"
            value={selectedState}
            onChange={handleStateChange}
            placeholder="Enter state name"
          />
          <button
            className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-md transition duration-300"
            onClick={handleShowPlot}
          >
            Show Plot
          </button>
        </div>
        {/* Display the plot image if the URL is available */}
        {plotUrl !== '' && (
          <div className="text-center">
            <img src={`http://localhost:5000/${plotUrl}`} alt="Rainfall Plot" className="max-w-full" />
          </div>
        )}
      </div>

      {/* Footer */}
      <footer className="bg-gradient-to-r from-slate-900 to-purple-900 p-4 mt-8">
        <div className="container mx-auto text-white text-center">
          &copy; {new Date().getFullYear()} Rainfall Data Visualization | All rights reserved.
        </div>
      </footer>
    </div>
  );
};


export default App;
